import Nav from "react-bootstrap/Nav";
import Popup from "reactjs-popup";
import { useEffect, useState } from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import React from "react";
import Login from "./loginsignup";
import axios from "axios";
function Navigation() {
  const [login, setlogin] = useState("");
  useEffect(() => {
    axios
      .post("http://localhost:8000/check")
      .then((response) => {
        console.log(setlogin(response.data));
      })
      .catch((error) => {
        "error";
      });
  });

  return (
    <>
      <Container>
        <Row>
          <Col>
            <Nav className="justify-content-center" activeKey="/home">
              <Nav.Item>
                <Nav.Link href="/movies">Movies</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="link-1">Stream</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="link-2">Event</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="link-3">Plays</Nav.Link>
              </Nav.Item>
            </Nav>
          </Col>
          <Col>
            <Nav className="justify-content-center" activeKey="/home">
              <Nav.Item>
                <Nav.Link href="/home">List Your Shows</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="link-1">Gift Card</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="link-2">Offers</Nav.Link>
              </Nav.Item>

              <Nav.Item>
                <Popup
                  trigger={<Nav.Link eventKey="link-3">Login/SignUp</Nav.Link>}
                  modal
                  nested
                  closeOnDocumentClick
                >
                  <div>
                    <Login />
                  </div>
                </Popup>
              </Nav.Item>
            </Nav>
          </Col>
        </Row>
      </Container>
    </>
  );
}

export default Navigation;
