import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import InputGroup from "react-bootstrap/InputGroup";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";
export default function SignUp() {
  const [username, setusername] = useState("");
  const [name, setname] = useState("");
  const [email, setemail] = useState("");
  const [number, setpnumber] = useState("");
  const [password, setpassword] = useState("");
  function username1(e) {
    setusername(e.target.value);
  }
  function password1(e) {
    setpassword(e.target.value);
  }

  function name1(e) {
    setname(e.target.value);
  }
  function email1(e) {
    setemail(e.target.value);
  }
  function number1(e) {
    setpnumber(e.target.value);
  }

  let navigate = useNavigate();
  function submit(e) {
    e.preventDefault();
    let path = `/`;
    navigate(path);
    e.preventDefault();
    axios
      .post("http://localhost:8000/user", {
        name: username,
        password: password,
      
    
    })
      .then((response) => {
        console.log("response");
      })
      .catch((error) => {
        "error";
      });
  }

  return (
    <Form style={{ margin: "200px 200px 200px 200px" }}>
      <Row className="mb-3">
        <Form.Group as={Col} controlId="formGridEmail">
          <Form.Label>Email</Form.Label>
          <Form.Control type="email" placeholder="Enter email" />
        </Form.Group>

        <Form.Group as={Col} onChange={password1} controlId="formGridPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" placeholder="Password" />
        </Form.Group>
      </Row>

      <Col xs="auto">
        <Form.Label
          onChange={username1}
          htmlFor="inlineFormInputGroup"
          visuallyHidden
        >
          Username
        </Form.Label>
        <InputGroup className="mb-2">
          <InputGroup.Text>@</InputGroup.Text>
          <Form.Control id="inlineFormInputGroup" placeholder="Username" />
        </InputGroup>
      </Col>

      <Form.Group onChange={name1} className="mb-3" controlId="formGridName">
        <Form.Label>Name</Form.Label>
        <Form.Control placeholder="Enter your name..." />
      </Form.Group>

      <Row className="mb-3">
        <Form.Group onChange={city1} as={Col} controlId="formGridCity">
          <Form.Label>City</Form.Label>
          <Form.Control />
        </Form.Group>

        <Form.Group onChange={number1} as={Col} controlId="formGridNumber">
          <Form.Label>Phone Number</Form.Label>
          <Form.Control />
        </Form.Group>
      </Row>

      <Form.Group className="mb-3" id="formGridCheckbox">
        <Form.Check type="checkbox" label="Check me out" />
      </Form.Group>

      <Button onClick={submit} variant="primary" type="submit">
        Submit
      </Button>
    </Form>
  );
}
